class TestVariableSizeNanograms:

    tests = tests
    param = None
    result = None
    i = None

    def test_all(self):
        for i,test in enumerate(self.tests):
            param, result = test
            r = Nonogram(*param).solve()
            assert r == result
            string = f'{i}: {r} Should_Equal {result} '
            string = string + ' Correct!' if r == result else string + ' Incorrect!'
            print(string,"\n")
